console.log("ðŸ” AllShopsPage.js file loaded");
throw new Error("Test error: is React rendering?");
import React, { useEffect, useState } from "react";
import { collection, getDocs } from "firebase/firestore";
import { db } from "../firebase";

const AllShopsPage = () => {
  const [shops, setShops] = useState([]);

  console.log("âœ… AllShopsPage rendering");
  useEffect(() => {
    const fetchShops = async () => {
      try {
        const snapshot = await getDocs(collection(db, "barbershops"));
        const shopList = snapshot.docs.map(doc => ({ id: doc.id, ...doc.data() }));
        setShops(shopList);
        console.log("Fetched shops:", shopList); // âœ… Debug output
      } catch (err) {
        console.error("Error fetching shops:", err);
      }
    };

    fetchShops();
  }, []);
console.log("Component loaded");

  return (
    <div className="p-6 max-w-3xl mx-auto">
      <h1 className="text-2xl font-bold mb-4">All Shops</h1>
      {shops.length === 0 ? (
        <p>No shops found.</p>
      ) : (
        <ul className="space-y-4">
          {shops.map(shop => (
            <li key={shop.id} className="border p-4 rounded shadow">
              <h2 className="text-xl font-semibold">{shop.name}</h2>
              <p><strong>Location:</strong> {shop.location}</p>
              {shop.address && (
                <p><strong>Address:</strong> {shop.address}</p>
              )}
              {shop.website && (
                <p>
                  <a 
                    href={shop.website} 
                    target="_blank" 
                    rel="noreferrer" 
                    className="text-blue-500 underline"
                  >
                    Visit website
                  </a>
                </p>
              )}
            </li>
          ))}
        </ul>
      )}
    </div>
  );
};

export default AllShopsPage;
