// src/components/HomePage.jsx
import React from "react";
import { useNavigate } from "react-router-dom";

function HomePage() {
  const navigate = useNavigate();

  const handleAddShop = () => {
    navigate("/submit");
  };

  const handleViewShops = () => {
    navigate("/shops");
  };

  return (
    <div className="relative min-h-screen bg-gray-50 text-gray-800 pb-32 px-6">
      {/* Content */}
      <div className="pt-8">
        {/* Hero Section */}
        <div className="text-center space-y-3">
          <h1 className="text-4xl font-bold">The Fade</h1>
          <p className="text-gray-600 italic">
            The global directory of Black barbershops.
          </p>
        </div>

        {/* Taglines */}
        <div className="mt-6 space-y-1 text-center">
          <p className="text-lg font-semibold text-gray-800">
            For the backpacker and the businessman.
          </p>
          <p className="text-lg font-semibold text-gray-800">
            For the student, the diplomat, the dad.
          </p>
          <p className="text-sm text-gray-700">
            Wherever you go, find a barber you can trust.
          </p>
        </div>

        {/* Join the Movement */}
        <div className="mt-10 space-y-3 text-left">
          <h3 className="text-lg font-bold text-gray-800">Join the Movement</h3>
          <p className="text-sm text-gray-700">
            We’re building the largest community-powered database of Black barbers in the world — and we need you.
          </p>
          <ul className="list-disc list-inside space-y-2 text-sm text-gray-700">
            <li>
              <strong>Submit a barbershop:</strong> Know a great shop? Add it to our directory.
            </li>
            <li>
              <strong>Search barbers by city:</strong> Launching soon in London, NYC, and Lagos — and expanding fast.
            </li>
            <li>
              <strong>Leave a review:</strong> Know a trusted and reliable barber?
            </li>
          </ul>
        </div>
      </div>

      {/* Fixed Bottom Buttons */}
      <div className="fixed bottom-6 left-0 right-0 px-6 flex flex-col items-center space-y-3">
        <button
          onClick={handleAddShop}
          className="bg-black text-white py-2 px-6 rounded hover:bg-gray-800 transition w-full max-w-xs"
        >
          Add a Barbershop
        </button>
        <button
          onClick={handleViewShops}
          className="bg-black text-white py-2 px-6 rounded hover:bg-gray-800 transition w-full max-w-xs"
        >
          View All Shops
        </button>
      </div>
    </div>
  );
}

export default HomePage;
