import React, { useState } from 'react';

export default function AddBarbershop() {
  const [formData, setFormData] = useState({
    name: '',
    address: '',
    city: '',
    country: '',
    website: '',
    instagram: '',
    description: '',
    submittedBy: '',
  });

  const [submitted, setSubmitted] = useState(false);

  const handleChange = (e) => {
    setFormData((prev) => ({
      ...prev,
      [e.target.name]: e.target.value,
    }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    // MVP: Replace with backend logic
    console.log('Submitted Barbershop:', formData);
    setSubmitted(true);
    setFormData({
      name: '',
      address: '',
      city: '',
      country: '',
      website: '',
      instagram: '',
      description: '',
      submittedBy: '',
    });
  };

  return (
    <div className="min-h-screen bg-gray-100 px-6 py-10">
      <div className="max-w-xl mx-auto bg-white shadow-md rounded-lg p-8">
        <h1 className="text-2xl font-bold mb-4">Add a Barbershop</h1>
        <p className="text-sm text-gray-600 mb-6">
          Know a great Black-owned barbershop? Add it to the directory.
        </p>

        {submitted && (
          <div className="bg-green-100 text-green-800 px-4 py-2 rounded mb-4">
            Thank you! The barbershop has been submitted.
          </div>
        )}

        <form onSubmit={handleSubmit} className="space-y-4">
          {[
            { label: 'Shop Name', name: 'name' },
            { label: 'Address', name: 'address' },
            { label: 'City', name: 'city' },
            { label: 'Country', name: 'country' },
            { label: 'Website (optional)', name: 'website' },
            { label: 'Instagram Handle (optional)', name: 'instagram' },
            { label: 'Submitted By (optional)', name: 'submittedBy' },
          ].map(({ label, name }) => (
            <div key={name}>
              <label className="block text-sm font-medium text-gray-700">{label}</label>
              <input
                type="text"
                name={name}
                value={formData[name]}
                onChange={handleChange}
                className="mt-1 block w-full px-4 py-2 border border-gray-300 rounded-md shadow-sm focus:ring-black focus:border-black"
              />
            </div>
          ))}

          <div>
            <label className="block text-sm font-medium text-gray-700">Description (optional)</label>
            <textarea
              name="description"
              rows={3}
              value={formData.description}
              onChange={handleChange}
              className="mt-1 block w-full px-4 py-2 border border-gray-300 rounded-md shadow-sm focus:ring-black focus:border-black"
            />
          </div>

          <button
            type="submit"
            className="w-full bg-black text-white py-2 px-4 rounded hover:bg-gray-800 transition-colors"
          >
            Submit
          </button>
        </form>
      </div>
    </div>
  );
}
