// src/components/HomePage.jsx
import React from "react";
import { useNavigate } from "react-router-dom";

function HomePage() {
  const navigate = useNavigate();

  const handleAddShop = () => {
    navigate("/submit");
  };

  const handleViewShops = () => {
    navigate("/shops");
  };

  return (
    <div className="min-h-screen bg-gray-50 text-gray-800 px-6 py-12">
      {/* Hero Section */}
      <div className="text-center space-y-4">
        <h1 className="text-4xl font-bold">The Fade</h1>
        <p className="text-gray-600 italic">
          The global directory of Black barbershops.
        </p>
      </div>

      {/* Taglines */}
      <div className="mt-10 space-y-1 text-center">
        <p className="text-xl font-semibold text-gray-800">
          For the backpacker and the businessman.
        </p>
        <p className="text-xl font-semibold text-gray-800">
          For the student, the diplomat, the dad.
        </p>
        <p className="text-md text-gray-700">
          Wherever you go, find a barber you can trust.
        </p>
      </div>

      {/* Join the Movement */}
      <div className="mt-12 space-y-4 text-left">
        <h3 className="text-xl font-bold text-gray-800">Join the Movement</h3>
        <p className="text-gray-700">
          We’re building the largest community-powered database of Black barbers in the world — and we need you.
        </p>
        <ul className="list-disc list-inside space-y-2 text-gray-700">
          <li>
            <strong>Submit a barbershop:</strong> Know a great shop? Add it to our directory.
          </li>
          <li>
            <strong>Search barbers by city:</strong> Launching soon in London, NYC, and Lagos — and expanding fast.
          </li>
          <li>
            <strong>Leave a review:</strong> Know a trusted and reliable barber?
          </li>
        </ul>
      </div>

      {/* Buttons */}
      <div className="mt-10 space-y-4 text-center">
        <button
          onClick={handleAddShop}
          className="bg-black text-white py-2 px-6 rounded hover:bg-gray-800 transition"
        >
          Add a Barbershop
        </button>
        <br />
        <button
          onClick={handleViewShops}
          className="bg-black text-white py-2 px-6 rounded hover:bg-gray-800 transition"
        >
          View All Shops
        </button>
      </div>
    </div>
  );
}

export default HomePage;
