import React, { useState } from "react";

const SubmitPage = () => {
  const [formData, setFormData] = useState({
    name: "",
    city: "",
    country: "",
    description: "",
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prev) => ({
      ...prev,
      [name]: value,
    }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    // Later: send this data to Firebase
    console.log("Submitted Barbershop:", formData);
    alert("Thank you! Your barbershop has been submitted.");
    setFormData({
      name: "",
      city: "",
      country: "",
      description: "",
    });
  };

  return (
    <div className="p-8 max-w-xl mx-auto">
      <h1 className="text-3xl font-bold mb-6">Submit a Barbershop</h1>
      <form onSubmit={handleSubmit} className="space-y-4">
        <div>
          <label className="block font-medium text-gray-700">Shop Name</label>
          <input
            type="text"
            name="name"
            required
            value={formData.name}
            onChange={handleChange}
            className="w-full border border-gray-300 rounded px-3 py-2 mt-1"
            placeholder="e.g. Chop City"
          />
        </div>
        <div>
          <label className="block font-medium text-gray-700">City</label>
          <input
            type="text"
            name="city"
            required
            value={formData.city}
            onChange={handleChange}
            className="w-full border border-gray-300 rounded px-3 py-2 mt-1"
            placeholder="e.g. Lagos"
          />
        </div>
        <div>
          <label className="block font-medium text-gray-700">Country</label>
          <input
            type="text"
            name="country"
            required
            value={formData.country}
            onChange={handleChange}
            className="w-full border border-gray-300 rounded px-3 py-2 mt-1"
            placeholder="e.g. Nigeria"
          />
        </div>
        <div>
          <label className="block font-medium text-gray-700">
            Description (optional)
          </label>
          <textarea
            name="description"
            value={formData.description}
            onChange={handleChange}
            rows={4}
            className="w-full border border-gray-300 rounded px-3 py-2 mt-1"
            placeholder="e.g. Known for clean fades and community vibes."
          />
        </div>
        <button
          type="submit"
          className="bg-black text-white font-semibold py-2 px-4 rounded-lg hover:bg-gray-800 transition duration-200"
        >
          Submit Shop
        </button>
      </form>
    </div>
  );
};

export default SubmitPage;
