// src/components/SubmitPage.jsx
import React, { useState } from "react";
import { db } from "../firebase";
import { addDoc, collection, serverTimestamp } from "firebase/firestore";
import { useNavigate } from "react-router-dom";

export default function SubmitPage() {
  const navigate = useNavigate();
  const [formData, setFormData] = useState({
    name: "",
    address: "",
    city: "",
    country: "",
    website: "",
    instagram: "",
    description: "",
    submittedBy: "",
  });

  const [isSubmitting, setIsSubmitting] = useState(false);
  const [errorMsg, setErrorMsg] = useState("");
  const [successMsg, setSuccessMsg] = useState("");

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((p) => ({ ...p, [name]: value }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setErrorMsg("");
    setSuccessMsg("");
    if (!formData.name.trim()) {
      setErrorMsg("Shop name is required.");
      return;
    }

    try {
      setIsSubmitting(true);
      await addDoc(collection(db, "barbershops"), {
        name: formData.name.trim(),
        address: formData.address.trim(),
        city: formData.city.trim(),
        country: formData.country.trim(),
        website: formData.website.trim(),
        instagram: formData.instagram.trim(),
        description: formData.description.trim(),
        submittedBy: formData.submittedBy.trim(),
        createdAt: serverTimestamp(),
      });

      setSuccessMsg("Thanks! Your barbershop has been submitted.");
      // Optional: small pause so user sees success, then redirect
      setTimeout(() => navigate("/"), 600);

      // Clear the form (in case user stays)
      setFormData({
        name: "",
        address: "",
        city: "",
        country: "",
        website: "",
        instagram: "",
        description: "",
        submittedBy: "",
      });
    } catch (err) {
      console.error(err);
      setErrorMsg("Something went wrong while saving. Please try again.");
    } finally {
      setIsSubmitting(false);
    }
  };

  return (
    <div className="min-h-screen bg-gray-50 py-10">
      <div className="mx-auto max-w-3xl rounded-xl bg-white p-8 shadow">
        <h1 className="text-3xl font-extrabold text-gray-900">Add a Barbershop</h1>
        <p className="mt-2 text-gray-600">
          Know a great Black-owned barbershop? Add it to the directory.
        </p>

        {errorMsg && (
          <div className="mt-4 rounded-md bg-red-50 p-3 text-red-700">{errorMsg}</div>
        )}
        {successMsg && (
          <div className="mt-4 rounded-md bg-green-50 p-3 text-green-700">{successMsg}</div>
        )}

        <form onSubmit={handleSubmit} className="mt-8 space-y-6">
          <Field label="Shop Name" name="name" value={formData.name} onChange={handleChange} required />
          <Field label="Address" name="address" value={formData.address} onChange={handleChange} />
          <Field label="City" name="city" value={formData.city} onChange={handleChange} />
          <Field label="Country" name="country" value={formData.country} onChange={handleChange} />
          <Field label="Website (optional)" name="website" value={formData.website} onChange={handleChange} />
          <Field label="Instagram Handle (optional)" name="instagram" value={formData.instagram} onChange={handleChange} />
          <TextArea label="Description (optional)" name="description" value={formData.description} onChange={handleChange} />
          <Field label="Your Name or Email (optional)" name="submittedBy" value={formData.submittedBy} onChange={handleChange} />

          <button
            type="submit"
            disabled={isSubmitting}
            className="w-full rounded-lg bg-black px-5 py-3 font-semibold text-white hover:bg-gray-800 disabled:opacity-60"
          >
            {isSubmitting ? "Submitting..." : "Submit Barbershop"}
          </button>
        </form>
      </div>
    </div>
  );
}

function Field({ label, name, value, onChange, required = false }) {
  return (
    <div>
      <label className="mb-1 block text-sm font-medium text-gray-700">
        {label} {required && <span className="text-red-500">*</span>}
      </label>
      <input
        className="block w-full rounded-lg border border-gray-300 px-4 py-2 focus:border-black focus:outline-none"
        type="text"
        name={name}
        value={value}
        onChange={onChange}
        required={required}
      />
    </div>
  );
}

function TextArea({ label, name, value, onChange }) {
  return (
    <div>
      <label className="mb-1 block text-sm font-medium text-gray-700">{label}</label>
      <textarea
        className="block w-full rounded-lg border border-gray-300 px-4 py-2 focus:border-black focus:outline-none"
        rows={4}
        name={name}
        value={value}
        onChange={onChange}
      />
    </div>
  );
}
