// src/components/HomePage.jsx
import React from "react";
import { useNavigate } from "react-router-dom";

function HomePage() {
  const navigate = useNavigate();

  const handleAddShop = () => {
    navigate("/submit");
  };

  const handleViewShops = () => {
    navigate("/allshops");
  };

  return (
    <div className="min-h-screen bg-gray-50 flex flex-col justify-between px-4 py-10">
      {/* Main Content */}
      <div className="max-w-3xl mx-auto text-center space-y-10">
        {/* Hero */}
        <div className="space-y-3">
          <h1 className="text-5xl font-extrabold text-gray-900">The Fade</h1>
          <p className="text-lg italic text-gray-600">
            The global directory of Black barbershops.
          </p>
        </div>

        {/* Taglines */}
        <div className="space-y-2 text-xl text-gray-900 font-semibold">
          <p>For the backpacker and the businessman.</p>
          <p>For the student, the diplomat, the dad.</p>
          <p className="text-base font-normal text-gray-700">
            Wherever you go, find a barber you can trust.
          </p>
        </div>

        {/* Movement */}
        <div className="text-left space-y-2 text-gray-800">
          <h3 className="text-2xl font-bold">Join the Movement</h3>
          <p className="text-base">
            We’re building the largest community-powered database of Black barbers in the world — and we need you.
          </p>
          <ul className="list-disc list-inside space-y-1 text-base">
            <li>
              <strong>Submit a barbershop:</strong> Know a great shop? Add it to our directory.
            </li>
            <li>
              <strong>Search barbers by city:</strong> Launching soon in London, NYC, and Lagos — and expanding fast.
            </li>
            <li>
              <strong>Leave a review:</strong> Know a trusted and reliable barber?
            </li>
          </ul>
        </div>
      </div>

      {/* Buttons */}
      <div className="flex justify-center gap-4 mt-10 mb-4">
        <button
          onClick={handleAddShop}
          className="bg-black text-white px-6 py-3 rounded-lg text-lg font-semibold"
        >
          Add a Barbershop
        </button>
        <button
          onClick={handleViewShops}
          className="bg-black text-white px-6 py-3 rounded-lg text-lg font-semibold"
        >
          View All Shops
        </button>
      </div>
    </div>
  );
}

export default HomePage;
